sys_script_include.script:
function validateEmailAddress(emailString) {
	//Ensure email value is a string.
	emailString += '';

	//Compare value against RegEx rules for email syntax.
	if(emailString.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
		return true;
	}
	else {
		return false;
	}

}
